import React, { useContext, useState, useEffect } from 'react'
import { DataContext } from '../DataProvider'
import { Link } from 'react-router-dom'
import '../css/Details.css'

const Details = props => {
    const {
        params: { id }
    } = props.match;
    const { product, setProductId, addToCart } = useContext(DataContext)

    useEffect(() => {
        setProductId(id);
    }, [id, setProductId]);

    const {
        name, 
        price,
        color,
        shortDescription,
        description,
        productImage
    } = product;
    console.log(product);

    return (
        <>

                product.map(item => (
                    <div className="details" key={item.id}>
                        <div className="product_img">
                        <img src={`data:image/jpeg;base64,${product.productImage[1]}`} />
                        </div>
                        <div className="box">
                            <div className="row">
                                <h2>{item.name}</h2>
                                <span>${item.price}</span>
                            </div>
                            <p>{item.shortDescription}</p>
                            <p>Color: <span>{item.color}</span></p>
                            <p>{item.content}</p>

                            {/* <div className="images" ref={this.myRef}>
                                {
                                    item.src.map((img, index) => (
                                        <img src={img} alt="" key={index}
                                            onClick={() => this.handleTab(index)}
                                        />
                                    ))
                                }
                            </div> */}

                            <Link to="/cart" className="cart" onClick={() => addToCart(item.id)}>
                                Add to cart
                                </Link>
                        </div>
                    </div>
                ))

        </>
    )
}

export default Details
