import React, { useState, useEffect } from 'react'
import { createContext } from 'react'

export const UserContext = createContext()

export const UserProvider = (props) => {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [forgotEmail, setForgotEmail] = useState("")

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("")

    const [currentEmail, setCurrentEmail] = useState("")

    const getCurrentUser = () => {
        fetch('user', {
            method: 'GET',
        }).then(response => {
            const { currentEmail } = response.data;
                setCurrentEmail({
                    email: currentEmail,
                });
        });
    }

    useEffect(() => {
        getCurrentUser()
    }, [])

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = { email, password };

        fetch('login', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(data)
        }).then(() => {
            console.log('user-info: ', data)
            localStorage.setItem('user-info: ', JSON.stringify(data))
            getCurrentUser()
        })
    }

    const forgotSubmit = (e) => {
        e.preventDefault();
        const data = { forgotEmail };

        fetch('forgot_password', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: data
        }).then(() => {
            console.log('your email: ', data)
        })
    }

    return (
        <UserContext.Provider value={{ show, email, getCurrentUser, currentEmail, handleClose, forgotSubmit, setForgotEmail, handleSubmit, setEmail, setPassword, handleShow }}>
            {props.children}
        </UserContext.Provider>
    );
}
