import React, { useState, useEffect } from 'react'
import { createContext } from 'react'

export const UserContext = createContext()

export const UserProvider = (props) => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("")

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [forgotEmail, setForgotEmail] = useState("")

    // const getCurrentUser = () => {
    //     $.ajax({
    //         type: 'GET',
    //         url: 'user',
    //     }).done(responseData => {
    //         const { isAuthenticated, email } = responseData.data;
    //         if (isAuthenticated) {
    //             setEmail({
    //                 email: email,
    //             });
    //         }
    //     });
    // }

    // useEffect(() => {
    //     getCurrentUser()
    // }, [])

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = { email, password };

        $.ajax('login', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(data)
        }).then(() => {
            console.log('user-info: ', data)
            localStorage.setItem('user-info: ', JSON.stringify(data))
        })
    }

    const forgotSubmit = (e) => {
        e.preventDefault();
        const data = { forgotEmail };

        fetch('forgot_password', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: data
        }).then(() => {
            console.log('your email: ', data)
        })
    }

    return (
        <UserContext.Provider value={{ show, email, handleClose, forgotSubmit, setForgotEmail, handleSubmit, setEmail, setPassword, handleShow }}>
            {props.children}
        </UserContext.Provider>
    );
}
