                    label="Primary"
                /> */}
                    <Button type='submit' variant='dark' style={btnstyle} className="btn-block">Log in</Button>
                    <a href="/oauth2/authorization/google">Sign in with Google</a>
                    <Typography>
                        <NavLink to="/reset_password?token=6htmP0qGuuU4ArpcWZudBeKyjI5boq" style={linkButton}>
                            Forget password?
                        </NavLink>
                    </Typography>
                    <Typography> Do you have an account?&nbsp;
                    <NavLink to="/signup">Sign up?</NavLink>
                    </Typography>
                </form>
            </Grid>
        </>
    );
}

export default ModalForgot;
