import React, { Component } from 'react';
import './css/Order.css';


export const DataContext = React.createContext();

export class DataProvider extends Component {

    state = {
        products: [
        ],
        isLoaded: false,
        cart: [],
        total: 0
    }


    fetchProducts = () => {
        fetch('https://online-store-120.herokuapp.com/store/products')
        .then((response) => response.json())
        .then(data => {
            this.setState({ products: data });
        });
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/posts')
          .then(res => res.json())
          .then(result => {
            this.setState({
              isLoaded: true,
              products: result
            });
          });
      }

    addCart = (id) => {
        const { products, cart } = this.state;
        const check = cart.every(item => {
            return item._id !== id
        })
        if (check) {
            const data = products.filter(product => {
                return product._id === id
            })
            this.setState({ cart: [...cart, ...data] })
        } else {
            alert("The product has been added to cart.")
        }
    };

    decrease = id => {
        const { cart } = this.state;
        cart.forEach(item => {
            if (item._id === id) {
                item.count === 1 ? item.count = 1 : item.count -= 1;
            }
        })
        this.setState({ cart: cart });
        this.getTotal();
    }

    increase = id => {
        const { cart } = this.state;
        cart.forEach(item => {
            if (item._id === id) {
                item.count += 1;
            }
        })
        this.setState({ cart: cart });
        this.getTotal();
    }

    getTotal = () => {
        const { cart } = this.state;
        const res = cart.reduce((prev, item) => {
            return prev + (item.price * item.count);
        }, 0)
        this.setState({ total: res })
    }

    removeProduct = id => {
        if (window.confirm("Do you want to delete this product?")) {
            const { cart } = this.state;
            cart.forEach((item, index) => {
                if (item._id === id) {
                    cart.splice(index, 1)
                }
            })
            this.setState({ cart: cart })
            this.getTotal();
        }
    }

    componentDidUpdate() {
        localStorage.setItem('dataCart', JSON.stringify(this.state.cart))
        localStorage.setItem('dataTotal', JSON.stringify(this.state.total))
    }

    componentDidMount() {
        const dataCart = JSON.parse(localStorage.getItem('dataCart'));
        if (dataCart !== null) {
            this.setState({ cart: dataCart });
        }

        const dataTotal = JSON.parse(localStorage.getItem('dataTotal'));
        if (dataTotal !== null) {
            this.setState({ total: dataTotal });
        }
    }

    render() {
        const { products, cart, total } = this.state;
        const { addCart, decrease, increase, removeProduct, getTotal } = this;
        return (
            <DataContext.Provider value={{ products, addCart, cart, decrease, increase, removeProduct, total, getTotal }}>
                {this.props.children} 
            </DataContext.Provider>
        )
    }
}
