import React, { useEffect, useState } from 'react';
import { InputLabel, Select, MuneItem, Button, Grid, Typography } from '@material-ui/core'
import { useForm, FormProvider } from 'react-hook-form';
import { Country, City } from 'country-state-city';

import FormInput from './FormInput';

export const PageContext = React.createContext();

export const PageContextProvider = (props) => {
    const [countries, setCountries] = useState([]);
    const [cities, setCities] = useState([]);
    const [selectedCountry, setSelectedCountry] = useState('');
    const [location, setLocation] = useState({
        country: '',
        city: ''
    })
    useEffect(() => {
        setCountries(Country.getAllCountries());
    }, [])

    const selectCountry = e => {
        let value = e.target.value;
        setSelectedCountry(value);
        setCities(City.getCitiesOfCountry(value));
        let countryName = countries.filter(el => el.isoCode === value)[0]?.name;
        setLocation({ ...location, country: countryName, city: '' })
    }

    const selectCity = e => {
        setLocation({ ...location, city: e.target.value });
    }

    const AddressForm = () => {
        const methods = useForm();

        return (
            <div style={{ margin: "30px" }}>
                <Typography variant="h6" gutterBottom>Shipping Address</Typography>
                <FormProvider {...methods}>
                    <form onSubmit=''>
                        <Grid container spacing={3}>
                            <FormInput required name='firstName' label='First name' />
                            <FormInput required name='lastName' label='Last name' />
                            <FormInput required name='address1' label='Address' />
                            <FormInput required name='email' label='Email' />
                            <FormInput required name='city' label='City' />
                            <FormInput required name='zip' label='ZIP / Postal code' />
                            <Grid item xs={12} sm={6}>
                                <InputLabel>Shipping Country</InputLabel>
                                <PageContext.Provider value={{location, setLocation}}>
                                <h4 className={'mt-2'}>Please select your location:</h4>
                                <Select onChange={selectCountry} className={'my-3'}>
                                    {countries.map(el => <option key={el.isoCode} value={el.isoCode}>{el.name}</option>)}
                                </Select>
                                <br />
                                {selectedCountry ? <Select onChange={selectCity}>
                                    {cities.map(el => <option key={el.name + '_' + el.stateCode} value={el.name}>{el.name}</option>)}
                                </Select> : ''}
                                {props.children}
                                </PageContext.Provider>
                            </Grid>
                        </Grid>
                    </form>
                </FormProvider>
            </div>
        )
    }
}
