import React from 'react'
import { Grid, Typography, Avatar, TextField, Button, Checkbox } from '@material-ui/core'
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import {Link} from 'react-router-dom'
import {useState} from 'react'

const SignUp = () => {
    const paperStyle = { margin: '0 auto' }
    const headerStyle = { margin: '0' }
    const avatarStyle = { backgroundColor: 'lightseagreen' }
    const marginTop = { marginTop: '5px' }



    const onSignUp = (e) => {
        e.preventDefault();
        const data = { name, email, password }
    }

    return (
        <Grid>
            <Typography elevation={20} className='paperStyle' style={paperStyle}>
                <Grid align='center'>
                    <Avatar style={avatarStyle}>
                        <AddCircleOutlineIcon />
                    </Avatar>
                    <h2 style={headerStyle}>Sign Up</h2>
                    <Typography variant='caption' gutterBottom>Please fill this form to create an account</Typography>
                </Grid>
                <form>
                    <TextField type="text" name="name" onChange={(e) => setName(e.target.value)} label='Name' placeholder='Enter your name' fullWidth required />
                    <TextField type="email" name="email" onChange={(e) => setEmail(e.target.value)} fullWidth label='Email' required />
                    <FormControl component="fieldset" style={marginTop}>
                        <FormLabel component="legend">Gender</FormLabel>
                        <RadioGroup aria-label="gender" name="gender" style={{ display: 'initial' }}>
                            <FormControlLabel value="female" control={<Radio />} label="FEMALE" />
                            <FormControlLabel value="male" control={<Radio />} label="MALE" />
                        </RadioGroup>
                    </FormControl>
                    <TextField fullWidth name="phoneNumber" label='Phone Number' />
                    <TextField fullWidth name="password" type='password' label='Password' />
                    <TextField fullWidth name="confirmPassword" type='password' label='Confirm Passord' />
                    <FormControlLabel
                        control={<Checkbox name="termsAndConditions" />}
                        label="I accept the terms and conditions."
                    />
                    <Button type='submit' variant='contained' color='primary' fullWidth>Sign Up</Button>
                    <Typography style={{marginTop: '10px'}}>Already have an account?&nbsp;
                    <Button component={Link} to="/login">Log in</Button>
                    </Typography>
                </form>
            </Typography>
        </Grid>
    )
}

export default SignUp
