import React, {useState, useHistory, useEffect} from 'react'
import { createContext } from 'react'

export const UserContext = createContext()

export const UserProvider = (props) => {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("")
    const history = useHistory();

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [forgotEmail, setForgotEmail] = useState("")


    const handleSubmit = (e) => {
        e.preventDefault();
        const data = { email, password };

        fetch('login', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(data)
        }).then(() => {
            console.log('user-info: ', data)
            localStorage.setItem('user-info: ', JSON.stringify(data))
            history.push({ pathname: "/user", state: { data: data } })
        })
    }

    useEffect(() => {
        if (localStorage.getItem('user-info')) {
            history.push("/user")
        }
    }, [])

    const forgotSubmit = (e) => {
        e.preventDefault();
        const data = { forgotEmail };

        fetch('forgot_password', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: data
        }).then(() => {
            console.log('your email: ', data)
            // localStorage.setItem('user-info: ', JSON.stringify(data))
            // history.push({ pathname: "/user", state: { data: data } })
        })
    }

    return (
        <UserContext.Provider value={{show, handleClose, forgotSubmit, setForgotEmail, handleSubmit, setEmail,  }}>
          {props.children}
        </UserContext.Provider>
      );
}
