import React, { useState } from 'react'
import { createContext } from 'react'
import * as Yup from 'yup'

export const UserContext = createContext()

export const UserProvider = (props) => {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [forgotEmail, setForgotEmail] = useState("")

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("")

    // const initialValues = {
    //     email: '',
    //     password: '',
    // }

    // const getCurrentUser = async function () {
    //     const currentUser = await Parse.User.currentAsync();
    //     if (currentUser !== null) {
    //       Alert.alert(
    //         'Success!',
    //         `${currentUser.get('username')} is the current user!`,
    //       );
    //     }
    //     return currentUser;
    //   };

    const validationSchema = Yup.object().shape({
        email: Yup.string().email('please enter valid email').required("Required"),
        password: Yup.string().required("Required")
    })

    const onSubmit = (values, props) => {
        console.log(values)
        setTimeout(() => {
            props.resetForm()
            props.setSubmitting(true)
        }, 2000)
        console.log(props)

        fetch('login', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(values)
        }).then(() => {
            console.log('user-info: ', values)
            localStorage.setItem('user-info: ', JSON.stringify(values))
        })
    }

    const forgotSubmit = (e) => {
        e.preventDefault();
        const data = { forgotEmail };

        fetch('forgot_password', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: data
        }).then(() => {
            console.log('your email: ', data)
        })
    }

    return (
        <UserContext.Provider value={{show, initialValues, validationSchema, onSubmit, handleClose, forgotSubmit, setForgotEmail, handleShow }}>
          {props.children}
        </UserContext.Provider>
      );
}
