import React, { useContext, useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { DataContext } from '../DataProvider'
import '../css/Products.css'
import { CarouselList } from './CarouselList'
import { PaginationButtons } from './Pagination'
import { ModalCart } from './Modals/ModalCart'
import { Modal, Button, Form } from 'react-bootstrap'

const ProductList = () => {
    const { data, addToCart, handlePageChange, currentPage, openModal, closeModal } = useContext(DataContext)
    const [scroll, setScroll] = useState(0);
    // const [show, setShow] = useState(true);

    // const handleClose = () => setShow(false);
    // const handleShow = () => setShow(true);

    const handleScroll = () => {
        setScroll(window.scrollY);
    };

    const handleUpButton = () => {
        window.scrollTo(0, 0);
    };

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    return (
        <>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Forgot Password</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" placeholder="Enter email" />
                </Modal.Body>
                <Modal.Footer>
                    {/* <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button> */}
                    <Button type="submit" variant="dark" onClick={handleClose}>
                        Submit
                    </Button>
                </Modal.Footer>
            </Modal>
            <div className="onTop">
                <button
                    className={scroll < 300 ? "" : "show"}
                    onClick={handleUpButton}>
                    Go Up
            </button>
            </div>

            <CarouselList />
            <div id="product">
                {
                    data.map(product => (
                        <div className="card" key={product.id}>
                            <Link to={`/products/${product.id}`}>
                                <img src={`data:image/jpeg;base64,${product.productImage[0]}`} />
                            </Link>
                            <div className="content">
                                <h3>
                                    <Link to={`/products/${product.id}`}>{product.name}</Link>
                                </h3>
                                <p>{product.shortDescription}</p>
                                <span>${product.price}</span><br />
                                <button onClick={() => {
                                    addToCart(product.id);

                                    }}>Add to cart</button>
                            </div>
                        </div>
                    ))
                }
            </div>
            <PaginationButtons
                handlePageChange={handlePageChange}
                page={currentPage}
            />
        </>
    );
};

export default ProductList;
