import React, { useContext, useState, useEffect } from 'react'
import { DataContext } from '../DataProvider'
import { Link, useParams } from 'react-router-dom'
import '../css/Details.css'

const Details = (props) => {
    const { data, product, setPath, addToCart } = useContext(DataContext)

    const { id } = useParams();

    console.log(+id)

    useEffect(() => {
        setPath(id)
    }, [id, setPath])

    const {
        name, color, shortDescription, price, description
    } = product;
    console.log(product)


    return (
        <>
            <div className="details" key={id}>
                <div className="box">
                    <div className="row">
                        <h2>{name}</h2>
                        <span>${price}</span>
                    </div>
                    <p>{shortDescription}</p>
                    <p>Color: <span>{color}</span></p>
                    <p>{description}</p>
                    <Link to="/cart" className="cart" onClick={() => addToCart(id)}>
                        Add to cart
                    </Link>
                </div>
            </div>

        </>
    )
}

export default Details
