import React, { useContext, useEffect } from 'react'
import { Grid, Typography, Avatar, TextField } from '@material-ui/core'
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import { NavLink, Link } from 'react-router-dom'
import './Login.css'
import { Button } from 'react-bootstrap'
import { UserContext } from './UserProvider'
import { Formik, Field, Form, ErrorMessage } from 'formik'
import { FormHelperText } from '@material-ui/core'
import * as Yup from 'yup'


const ResetPassword = () => {
    const { handleSubmit, setEmail, setPassword, handleShow } = useContext(UserContext)

    const paperStyle = { margin: '0 auto' }
    const btnstyle = { marginTop: '8px', marginBottom: '8px' }

    const initialValues = {
        password: '',
        confirmPassword: '',
    }

    const validationSchema = Yup.object().shape({
        password: Yup.string().required("Required"),
        confirmPassword: Yup.string().oneOf([Yup.ref("password")], "Password not matched").required("Required"),
    })

    const submitResetPassword = (values, props) => {
        console.log(values)
        console.log(props)

        fetch('https://online-store-120.herokuapp.com/reset_password', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(values)
        }).then(() => {
            console.log('new data added: ', values)
            localStorage.setItem('new data added: ', JSON.stringify(values))
        })
    }

    return (
        <>
            <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={submitResetPassword}>
                {(props) => (
                    <div className="userStyle">
                        <Form onSubmit={handleSubmit} className='paperStyle' style={paperStyle}>
                            <Grid align='center'>
                                <h2>Reset Password</h2>
                            </Grid>
                            <Field as={TextField} fullWidth name="password" type='password' label='Password'
                                helperText={<ErrorMessage name="password" />} />
                            <Field as={TextField} fullWidth name="confirmPassword" type='password' label='Confirm Passord'
                                helperText={<ErrorMessage name="confirmPassword" />} />
                            <Button type='submit' variant='dark' style={btnstyle} className="btn-block">Change Password</Button>
                        </Form>
                    </div>
                )}
            </Formik>
        </>
    )
}

export default ResetPassword
